import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import ProjectCard from '../components/ProjectCard'; // Now points to the index.jsx
import SkillItem from '../components/SkillItem';    // Points to index.jsx
import { fetchPortfolioData } from '../services/api';
import '../styles/global.css';
import '../styles/theme.css';

const PortfolioPage = () => {
  const [data, setData] = useState({ projects: [], skills: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        const portfolioData = await fetchPortfolioData();
        setData(portfolioData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, []);

  if (loading) return <div className="loading">Loading portfolio data...</div>;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <>
      <Header />
      <main className="container">
        <section id="projects" className="section">
          <h2 className="section-title">My Projects</h2>
          <div className="projects-grid">
            {data.projects.map(project => (
              <ProjectCard key={project._id} project={project} />
            ))}
          </div>
        </section>

        <section id="skills" className="section">
          <h2 className="section-title">Technical Skills</h2>
          <div className="skills-grid">
            {data.skills.map(skill => (
              <SkillItem key={skill._id} skill={skill} />
            ))}
          </div>
        </section>
      </main>
    </>
  );
};

export default PortfolioPage;
